import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  FaRegCircle,
  FaRegCheckCircle,
  FaRegTimesCircle,
  FaPlus
} from "react-icons/fa";

const TagForm = (props) => {
  const [tag, setTag] = useState("");
  const [formShow, setFormShow] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    let tags = props.todo.tags;
    if (!tags.includes(tag)) {
      tags.push(tag);
    }
    axios
      .post(`http://localhost:5000/todos/update/${props.todo._id}`, {
        task: props.todo.task,
        username: props.todo.username,
        due: props.todo.due,
        complete: props.todo.complete,
        tags: tags,
      })
      .then((res) => console.log(res));
    setTag('');
  };

  const handleChange = (e) => {
    setTag(e.target.value);
  };

  const showForm = () => {
    setFormShow(true);
  }

  return (
    <>
    <button class="btn tag-btn p-2 m-1" onClick={() => showForm()}>Add Tag <span class={`${formShow ? '' : ''} plus-button`}>+</span></button>
    <form class={`${formShow ? '' : 'd-none'}`} onSubmit={handleSubmit}>
      <input placeholder="Add Tag" value={tag} onChange={handleChange} />
    </form>
    </>
  );
};

const TodoList = (props) => {

  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState("");
  const [globalTags, setGlobalTags] = useState();

  const refreshTodos = () => {
    axios.get(`http://localhost:5000/todos/`).then((res) => {
      setTodos(
        res.data.filter((todo) => todo.username === props.user.username)
      );
    });
  };

  const refreshGlobalTags = () => {
    let tempTags = [];
    for (let i=0; i < todos.length; i++){
        for (let j=0; j<todos[i].tags.length; j++){
            if(!tempTags.includes(todos[i].tags[j])){
                tempTags.push(todos[i].tags[j])
            }
        }
    }
    setGlobalTags(tempTags)
  }

  useEffect(() => {
      refreshGlobalTags();
  }, [todos])

  useEffect(() => {
    refreshTodos();
  }, [todos]);

  const handleChange = (e) => {
    setTask(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/todos/add", {
        task,
        username: props.user.username,
      })
      .then((res) => console.log(res));
    setTask("");
  };

  const markComplete = (todo) => {
    axios
      .post(`http://localhost:5000/todos/update/${todo._id}`, {
        task: todo.task,
        username: todo.username,
        due: todo.due,
        tags: todo.tags,
        complete: !todo.complete,
      })
      .then((res) => console.log(res));
  };

  const removeTask = (id) => {
    axios
      .delete(`http://localhost:5000/todos/${id}`)
      .then((res) => console.log(res));
  };

  const removeTag = (todo, tag) => {
    axios.post(`http://localhost:5000/todos/update/${todo._id}`, {
        task: todo.task,
        username: todo.username,
        due: todo.due,
        complete: todo.complete,
        tags: todo.tags.filter(item => item !== tag)
      })
      .then((res) => console.log(res));
  }

  return (
    <div className="container mt-1">
        <div className="row">
            {JSON.stringify(globalTags)}
        </div>
      <div className="row justify-content-center">
        <div className="col-12">
          <form onSubmit={handleSubmit}>
            <input className="w-100" value={task} onChange={handleChange} />
          </form>
        </div>
      </div>
      <div className="container mt-5">
        {todos.map((todo) => (
          <div className="row justify-content-center p-2" key={todo._id}>
            <div className="col-1">
              <FaRegCircle
                size={30}
                className={`${todo.complete ? "d-none" : ""} hover-change`}
                onClick={() => markComplete(todo)}
              />
              <FaRegCheckCircle
                size={30}
                className={`${todo.complete ? "" : "d-none"} hover-change`}
                onClick={() => markComplete(todo)}
              />
            </div>
            <div className={`${todo.complete ? "complete" : ""} col-6`}>
              {todo.task}
            </div>
            <div className="col-4">
              {todo.tags.map((tag) => (
                <div key={tag} className="tag p-2 m-1">{tag}<span className="pointer" onClick={() => removeTag(todo, tag)}>  x</span></div>
              ))}
              <TagForm todo={todo} />
            </div>
            <div className="col-1">
              <FaRegTimesCircle
                size={30}
                className="hover-change"
                onClick={() => removeTask(todo._id)}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TodoList;
