import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.css'
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle';
import './App.css';

import TodoList from './components/TodoList'
import NavBar from './components/NavBar';

function App() {
  const [user, setUser] = useState('');
  const [users, setUsers] = useState([]);
  const [usernames, setUsernames] = useState([]);
  const [isLogin, setIsLogin] = useState(true);
  const [loginUser, setLoginUser] = useState('');
  const [loginPass, setLoginPass] = useState('');
  const [loginUserError, setLoginUserError] = useState(false);
  const [loginPassError, setLoginPassError] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:5000/users/`).then(res => {
      setUsers(res.data);
      setUsernames(res.data.map(user => user.username));
    })
  }, [])

  const handleLoginUserChange = (e) => {
    setLoginUser(e.target.value);
  }

  const handleLoginPassChange = (e) => {
    setLoginPass(e.target.value);
  }

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    if(!usernames.includes(loginUser)){
      setLoginUserError(true)
    } else {
      setLoginUserError(false)
      const user = users.find(user => user.username === loginUser)
      if(user.password !== loginPass){
        setLoginPassError(true)
      } else {
        setLoginPassError(false)
        setUser(user)
      }
    }
  }

  const logOut = () => {
    setUser('');
  }

  if(!user) {
  return (
    <Router>
      <NavBar user={user} logOut={logOut}/>
      <div className="container">
        <div className="row border rounded justify-content-center p-2">
          <div className="col-6 text-center">
            <button onClick={() => setIsLogin(true)} className={`${isLogin ? 'btn-dark': ''} btn w-25`}>Log In</button>
            <button onClick={() => setIsLogin(false)} className={`${isLogin ? '': 'btn-dark'} btn w-25`}>Sign Up</button>
            <form className={`${isLogin ? '': 'd-none'} my-4`} onSubmit={handleLoginSubmit}>
              <div className="form-group">
                <label>Username</label>
                <input type="text" className="form-control" id="loginUsername" onChange={handleLoginUserChange} />
                <small className={`${loginUserError ? '': 'd-none'} form-text`}>Please enter a valid username.</small>
              </div>
              <div className="form-group">
                <label>Password</label>
                <input type="password" className="form-control" id="loginPassword" onChange={handleLoginPassChange} />
                <small className={`${loginPassError ? '': 'd-none'} form-text`}>Password is incorrect.</small>
              </div>
              <button type="submit" className="btn btn-primary">Log In</button>
            </form>
          </div>
        </div>
      </div>
    </Router>
  )
  } else {
    return(
      <Router>
        <NavBar logOut={logOut}/>
        <TodoList user={user} />
      </Router>
    )
  }
}

export default App;
