import React, { useState, useEffect } from "react";
import axios from "axios";
import {FaRegCircle, FaRegCheckCircle, FaRegTimesCircle} from 'react-icons/fa'

const TodoList = (props) => {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState("");

  useEffect(() => {
    axios.get(`http://localhost:5000/todos/`).then((res) => {
      setTodos(
        res.data.filter((todo) => todo.username === props.user.username)
      );
    });
  }, [task]);

  const handleChange = (e) => {
    setTask(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/todos/add", {
        task,
        username: props.user.username,
      })
      .then((res) => console.log(res));
    setTask("");
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-12">
          <form onSubmit={handleSubmit}>
            <input className="w-100" value={task} onChange={handleChange} />
          </form>
        </div>
      </div>
      <div className="container">
        {todos.map((todo) => (
          <div className="row justify-content-center" key={todo._id}>
            <div className="col-1"><FaRegCircle className={`${todo.complete ? 'd-none': ''}`} /><FaRegCheckCircle className={`${todo.complete ? '': 'd-none'}`} /></div>
            <div className="col-6">{todo.task}</div>
            <div className="col-4">{todo.tags.map(tag => (
                <div>{tag}</div>
            ))}
            </div>
            <div className="col-1"><FaRegTimesCircle /></div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TodoList;
