import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.css'
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle';
import './App.css';

import TodoList from './components/TodoList'
import NavBar from './components/NavBar';

function App() {
  const [user, setUser] = useState('');
  const [users, setUsers] = useState([]);
  const [usernames, setUsernames] = useState([]);
  const [isLogin, setIsLogin] = useState(true);
  const [loginUser, setLoginUser] = useState('');
  const [loginPass, setLoginPass] = useState('');
  const [loginUserError, setLoginUserError] = useState(false);
  const [loginPassError, setLoginPassError] = useState(false);
  const [signupUser, setSignupUser] = useState('');
  const [signupPass1, setSignupPass1] = useState('');
  const [signupPass2, setSignupPass2] = useState('');
  const [signupUserError, setSignupUserError] = useState(false);
  const [signupPass1Error, setSignupPass1Error] = useState(false);
  const [signupPass2Error, setSignupPass2Error] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:5000/users/`).then(res => {
      setUsers(res.data);
      setUsernames(res.data.map(user => user.username));
    })
  }, [])

  const handleLoginUserChange = (e) => {
    setLoginUser(e.target.value);
  }

  const handleLoginPassChange = (e) => {
    setLoginPass(e.target.value);
  }

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    if(!usernames.includes(loginUser)){
      setLoginUserError(true)
    } else {
      setLoginUserError(false)
      const user = users.find(user => user.username === loginUser)
      if(user.password !== loginPass){
        setLoginPassError(true)
      } else {
        setLoginPassError(false)
        setUser(user)
      }
    }
  }

  const handleSignupSubmit = (e) => {
    e.preventDefault();
    if(usernames.includes(signupUser)){
      setSignupUserError(true)
    } else {
      setSignupUserError(false)
      if(signupPass1.length < 6){
        setSignupPass1Error(true)
      } else {
        setSignupPass1Error(false)
        if(signupPass1 !== signupPass2){
          setSignupPass2Error(true)
        } else {
          setSignupPass2Error(false)
          axios.post(`http://localhost:5000/users/add`, {
            username: signupUser,
            password: signupPass1
          }).then(res => console.log(res));
        }
      }
    }
  }

  const logOut = () => {
    setUser('');
  }

  if(!user) {
  return (
    <Router>
      <NavBar user={user} logOut={logOut}/>
      <div className="container login-container">
        <div className="row border rounded justify-content-center p-2">
          <div className="col-6 text-center">
            <button onClick={() => setIsLogin(true)} className={`${isLogin ? 'active-btn': ''} btn w-25`}>Log In</button>
            <button onClick={() => setIsLogin(false)} className={`${isLogin ? '': 'active-btn'} btn w-25`}>Sign Up</button>
            <form className={`${isLogin ? '': 'd-none'} my-4`} onSubmit={handleLoginSubmit}>
              <div className="form-group">
                <label>Username</label>
                <input type="text" className="form-control" id="loginUsername" onChange={handleLoginUserChange} />
                <small className={`${loginUserError ? '': 'd-none'} form-text`}>Please enter a valid username.</small>
              </div>
              <div className="form-group">
                <label>Password</label>
                <input type="password" className="form-control" id="loginPassword" onChange={handleLoginPassChange} />
                <small className={`${loginPassError ? '': 'd-none'} form-text`}>Password is incorrect.</small>
              </div>
              <button type="submit" className="btn">Log In</button>
            </form>
            <form className={`${isLogin ? 'd-none': ''} my-4`} onSubmit={handleSignupSubmit}>
              <div className="form-group">
                <label>Username</label>
                <input type="text" className="form-control" id="signupUsername" onChange={(e) => setSignupUser(e.target.value)} />
                <small className={`${signupUserError ? '': 'd-none'} form-text`}>Username already taken.</small>
              </div>
              <div className="form-group">
                <label>Password</label>
                <input type="password" className="form-control" id="signupPassword1" onChange={e => setSignupPass1(e.target.value)} />
                <small className={`${signupPass1Error ? '': 'd-none'} form-text`}>Password must be at least 6 characters long.</small>
              </div>
              <div className="form-group">
                <label>Re-enter Password</label>
                <input type="password" className="form-control" id="signupPassword2" onChange={e => setSignupPass2(e.target.value)} />
                <small className={`${signupPass2Error ? '': 'd-none'} form-text`}>Passwords don't match.</small>
              </div>
              <button type="submit" className="btn">Log In</button>
            </form>
          </div>
        </div>
      </div>
    </Router>
  )
  } else {
    return(
      <Router>
        <NavBar user={user} logOut={logOut}/>
        <TodoList user={user} />
      </Router>
    )
  }
}

export default App;
