import React, { useState, useEffect } from "react";
import axios from "axios";
import {FaRegCircle, FaRegCheckCircle, FaRegTimesCircle} from 'react-icons/fa'

const TodoList = (props) => {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState("");

  useEffect(() => {
    refreshTodos();
  });

  const refreshTodos = () => {
    axios.get(`http://localhost:5000/todos/`).then((res) => {
        setTodos(
          res.data.filter((todo) => todo.username === props.user.username)
        );
      });
  }

  const handleChange = (e) => {
    setTask(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/todos/add", {
        task,
        username: props.user.username,
      })
      .then((res) => console.log(res));
    setTask("");
  };

  const markComplete = (id, completed) => {
      axios.post(`http://localhost:5000/todos/update/${id}`, {
          completed: !completed
      }).then(res => console.log(res));
  }

  const removeTask = (id) => {
      axios.delete(`http://localhost:5000/todos/${id}`).then(res => console.log(res));
  }

  return (
    <div className="container mt-1">
      <div className="row justify-content-center">
        <div className="col-12">
          <form onSubmit={handleSubmit}>
            <input className="w-100" value={task} onChange={handleChange} />
          </form>
        </div>
      </div>
      <div className="container mt-5">
        {todos.map((todo) => (
          <div className="row justify-content-center p-2" key={todo._id}>
            <div className="col-1"><FaRegCircle size={30} className={`${todo.completed ? 'd-none': ''}`} onClick={() => markComplete(todo._id, todo.completed)}/><FaRegCheckCircle size={30} className={`${todo.completed ? '': 'd-none'}`} onClick={() => markComplete(todo._id, todo.completed)}/></div>
            <div className="col-6">{todo.task}</div>
            <div className="col-4">{todo.tags.map(tag => (
                <div>{tag}</div>
            ))}
            </div>
            <div className="col-1"><FaRegTimesCircle size={30} onClick={() => removeTask(todo._id)} /></div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TodoList;
