import React, { useState, useEffect } from "react";
import axios from "axios";
import DatePicker from "react-mobile-datepicker";
import { format } from "date-fns";
import $ from "jquery";
import {
  FaRegCircle,
  FaRegCheckCircle,
  FaPlusCircle,
  FaRegTrashAlt,
  FaChevronCircleDown,
  FaChevronCircleUp,
} from "react-icons/fa";

const url = "";

const CompleteButton = (props) => {
  const [hover, setHover] = useState(false);

  const hoverToggle = () => {
    setHover(!hover);
  };

  const handleClick = () => {
    props.markComplete(props.todo);
    setHover(false);
  };

  return (
    <>
      <FaRegCircle
        size={30}
        onMouseEnter={() => hoverToggle()}
        onMouseLeave={() => hoverToggle()}
        style={{
          color: hover ? props.user.color : "#292b2c",
          cursor: hover ? "pointer" : "default",
        }}
        className={props.todo.complete ? "d-none" : ""}
        onClick={handleClick}
      />
      <FaRegCheckCircle
        size={30}
        onMouseEnter={() => hoverToggle()}
        onMouseLeave={() => hoverToggle()}
        style={{
          color: hover ? props.user.color : "#292b2c",
          cursor: hover ? "pointer" : "default",
        }}
        className={props.todo.complete ? "" : "d-none"}
        onClick={handleClick}
      />
    </>
  );
};

const DateButton = (props) => {
  const [date, setDate] = useState(
    props.todo.due ? new Date(props.todo.due) : new Date()
  );
  const [isOpen, setIsOpen] = useState(false);

  const handleClick = () => {
    setIsOpen(true);
  };

  const handleCancel = () => {
    setIsOpen(false);
  };

  const handleChange = (e) => {
    const formattedDate = new Date(e.target.value.replace(/-/g, "/"));
    console.log(formattedDate);
    axios
      .post(`${url}/todos/update/${props.todo._id}`, {
        task: props.todo.task,
        username: props.todo.username,
        due: formattedDate,
        complete: props.todo.complete,
        tags: props.todo.tags,
      })
      .then((res) => console.log(res));
    setDate(formattedDate);
  };

  return (
    <>
      <input
        style={{marginLeft: -50}}
        className="date-btn-mobile mx-0"
        value={format(date, "yyyy-MM-dd")}
        type="date"
        onChange={handleChange}
      />
    </>
  );
};

const ToggleButton = (props) => {
  const [showTags, setShowTags] = useState(false);

  const tagToggle = () => {
    setShowTags(!showTags);
  };

  return (
    <>
      <button
        class="btn"
        data-toggle="collapse"
