import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.css'
import './App.css';

function App() {
  const [user, setUser] = useState('');
  const [users, setUsers] = useState([]);
  const [isLogin, setIsLogin] = useState(true);

  useEffect(() => {
    axios.get(`http://localhost:5000/users/`).then(res => {
      setUsers(res.data);
    })
  }, [])

  if(!user) {
  return (
    <Router>
      <p>{JSON.stringify(users)}</p>
      <div className="container">
        <div className="row border justify-content-center">
          <div className="col-6 text-center">
            <button onClick={() => setIsLogin(true)} className={`${isLogin ? 'btn-dark': ''} btn w-25`}>Log In</button>
            <button onClick={() => setIsLogin(false)} className={`${isLogin ? '': 'btn-dark'} btn w-25`}>Sign Up</button>
            <form>
              <div class="form-group">
                <label for="loginUsername">Username</label>
                <input type="text" class="form-control" id="loginUsername" />
              </div>
              <div class="form-group">
                <label for="loginPassword">Password</label>
                <input type="password" class="form-control" id="loginPassword" />
              </div>
            </form>
          </div>
        </div>
      </div>
    </Router>
  )
  } else {
    return(
      <Router>
        <p>Logged In</p>
      </Router>
    )
  }
}

export default App;
