import React, { useState, useEffect } from "react";
import axios from "axios";
import $ from "jquery";
import {
  FaRegCircle,
  FaRegCheckCircle,
  FaRegTimesCircle,
  FaPlusCircle,
  FaRegTrashAlt,
  FaChevronCircleDown,
  FaChevronCircleUp
} from "react-icons/fa";

const DeleteButton = (props) => {
  const [hover, setHover] = useState(false);

  const hoverToggle = () => {
    setHover(!hover);
  };

  return (
    <FaRegTimesCircle
      size={30}
      onMouseEnter={() => hoverToggle()}
      onMouseLeave={() => hoverToggle()}
      style={{
        color: hover ? props.user.color : "#292b2c",
        cursor: hover ? "pointer" : "default",
      }}
      onClick={() => props.removeTask(props.todo._id)}
    />
  );
};

const CompleteButton = (props) => {
  const [hover, setHover] = useState(false);

  const hoverToggle = () => {
    setHover(!hover);
  };

  const handleClick = () => {
    props.markComplete(props.todo);
    setHover(false);
  };

  return (
    <>
      <FaRegCircle
        size={30}
        onMouseEnter={() => hoverToggle()}
        onMouseLeave={() => hoverToggle()}
        style={{
          color: hover ? props.user.color : "#292b2c",
          cursor: hover ? "pointer" : "default",
        }}
        className={props.todo.complete ? "d-none" : ""}
        onClick={handleClick}
      />
      <FaRegCheckCircle
        size={30}
        onMouseEnter={() => hoverToggle()}
        onMouseLeave={() => hoverToggle()}
        style={{
          color: hover ? props.user.color : "#292b2c",
          cursor: hover ? "pointer" : "default",
        }}
        className={props.todo.complete ? "" : "d-none"}
        onClick={handleClick}
      />
    </>
  );
};

