import React, {useState} from 'react';
import {FaCheckDouble, FaTags, FaInbox} from 'react-icons/fa';
import {IoIosColorPalette} from 'react-icons/io'

const SwitchButton = (props) => {

    const [hover, setHover] = useState(false);

  const hoverToggle = () => {
    setHover(!hover);
  };

    return(
        <>
        <button class={`${props.user && props.isInbox ? '' : 'd-none'} btn`} style={{color: props.navColor, fontWeight: 'bold', border: `1px solid ${props.navColor}`}}onClick={() => props.handleTagsToggle()}>Tags <FaTags /></button>
        <button class={`${props.user && !props.isInbox ? '' : 'd-none'} btn`} style={{color: props.navColor, fontWeight: 'bold', border: `1px solid ${props.navColor}`}}onClick={() => props.handleTagsToggle()}>Inbox <FaInbox /></button>
        </>
    )
}

const NavBar = (props) => {

    const navColor = props.user ? props.user.color : 'black';

    return(
        <nav class="navbar mb-5 py-2">
            <a class="navbar-brand m-0 p-0" href="#" style={{color: navColor}}>do.ly<FaCheckDouble /></a>
            <div className="mr-1 row">
            <SwitchButton user={props.user} isInbox={props.isInbox} navColor={navColor} handleTagsToggle={props.handleTagsToggle} />
            <div className="dropdown my-auto">
            <IoIosColorPalette class={`${props.user ? '' : 'd-none'} dropdown-toggle my-auto mx-3`} data-toggle="dropdown" style={{color: navColor, height: 30, width: 30, borderRadius: 50, cursor: 'pointer'}} />
            <div className="dropdown-menu" style={{backgroundColor:'white', border:'1px solid black'}}>
                <div className="row justify-content-center">
                    <div className="m-1" onClick={(e) => props.setUserColor(e.target.style.backgroundColor)} style={{backgroundColor:'#B64B51', height: 30, width: 30}}></div>
                    <div className="m-1" onClick={(e) => props.setUserColor(e.target.style.backgroundColor)} style={{backgroundColor:'#5f956b', height: 30, width: 30}}></div>
                    <div className="m-1" onClick={(e) => props.setUserColor(e.target.style.backgroundColor)} style={{backgroundColor:'#4e83a0', height: 30, width: 30}}></div>
                </div>
                <div className="row justify-content-center">
                    <div className="m-1" onClick={(e) => props.setUserColor(e.target.style.backgroundColor)} style={{backgroundColor:'#b09f38', height: 30, width: 30}}></div>
                    <div className="m-1" onClick={(e) => props.setUserColor(e.target.style.backgroundColor)} style={{backgroundColor:'#b27e41', height: 30, width: 30}}></div>
                    <div className="m-1" onClick={(e) => props.setUserColor(e.target.style.backgroundColor)} style={{backgroundColor:'#856197', height: 30, width: 30}}></div>
                </div>
            </div>
            </div>
            <button class={`${props.user ? '' : 'd-none'} btn log-out`} style={{color: navColor, fontWeight: 'bold', border: `1px solid ${navColor}`}}onClick={() => props.logOut()}>Log Out</button>
            </div>
        </nav>
    )
}

export default NavBar
